INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Iced Latte', '60', 1, 1, 'coffee', 'A cool escape, this drink combines fresh espresso with icy milk for a creamy, refreshing pick-me-up. Perfect for a serene break any time.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Espresso', '51', 1, 1, 'coffee', 'Espresso is a vibrant, compact burst of coffee, turning lackluster mornings into go-getter days. It''s strong, snappy, and gets you going,remember to savor each sip.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Americano', '50', 1, 1, 'coffee', 'Americano coffee, originating from WWII American soldiers, is a simple yet flavorful blend of espresso and water, reflecting a piece of history and cultural fusion. Ideal for a refreshing morning or a relaxing afternoon pause, it offers a timeless coffee experience.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Cappuccino', '30', 1, 1, 'coffee', 'Cappuccino, an Italian specialty, presents a harmonious trio of espresso, steamed milk, and milk foam, delivering a rich, layered coffee experience. Savored typically at breakfast, it provides a smooth, foamy start to any day with its deep, aromatic flavors.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Latte', '30', 1, 1, 'coffee', 'Latte is espresso''s milder counterpart, softened by steamy milk for a soothing effect. It''s creamy, comforting, and perfect for easygoing mornings or relaxed afternoons.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Double Espresso', '30', 1, 1, 'coffee', 'Doppio, or Double Espresso, doubles down on flavor and energy. It''s a rich, powerful shot for those who want an extra boost. Ideal for coffee lovers looking for twice the buzz.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Chocolate Cold Brew', '30', 1, 1, 'coffee', 'Chocolate Cold Brew combines cold brew and ice for a refreshing start, topped with a rich chocolate milk foam for a luxurious twist. It''s a sweet, chilled journey in every sip,perfect for sunny days or when you need a sweet fix.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Foamy Hazelnut Latte', '30', 1, 1, 'coffee', 'Foamy Hazelnut Latte is a masterpiece of espresso, ice, and milk, whipped with hazelnut and condensed milk into a creamy, frothy delight. It''s the ultimate treat for those who love a sweet, cool latte.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Iced Americano', '30', 1, 1, 'coffee', 'Iced Americano offers a cool burst of espresso over ice, striking the right balance between strong coffee and refreshing chill. Ideal for a refreshing pick-me-up on warm days.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Iced Foam Latte', '30', 1, 1, 'coffee', 'A refreshing blend of iced milk and rich, frothy espresso. Sweet, condensed milk layers with icy smoothness for a quick, delightful coffee fix.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Coconut Latte', '30', 1, 1, 'coffee', 'A tropical take on the classic latte with creamy coconut milk and a hint of espresso sweetness. Garnish to your liking for an exotic coffee retreat.');
INSERT INTO "formula_duration" ("formula", "duration", "left_status", "right_status", "type", "introduction") VALUES ('Rose Orange Cold Brew', '30', 1, 1, 'coffee', 'An aromatic cold brew with a citrusy rose twist, creating a unique, floral-infused coffee experience. Ideal for those who love to try new, sophisticated flavors.');
